import { useState } from "react";
import {HomePageExplore} from "../../../data/homepage-explore"
import HighLightText from "./HighLightText";


const ExploreMore=()=>{

    const [currentTab,setCurrentTab]=useState(HomePageExplore[0].tag)
    const[course,setCourse]=useState(HomePageExplore[0].courses)
    const[currentCard,setCurrentCard]=useState(HomePageExplore[0].courses[0].heading)

    const setMyCard=(value)=>{
        setCurrentTab(value)
        const result=HomePageExplore.filter((course)=>course.tag==value)
        setCourse(result.courses)
        setCurrentCard(result.courses[0].heading)
    }
    
    return(
        <div>
            <div className="space-y-2">
                <div className="text-4xl font-semibold text-center">
                    Unlock the{" "}
                    <HighLightText text={" Power of Code"}/>
                </div>
                <p className="text-richblue-300 text-center text-lg">
                    Learn to Build Anything You Can Imagine
                </p>
            </div>
        </div>
    )
    }
export default ExploreMore;
