import { TypeAnimation } from 'react-type-animation'
import CTAButton from './Button'
// import HighLightText from './HighLightText'
import {FaArrowRight} from "react-icons/fa"


const CodeBlocks=({
    position,heading,subheading,ctabtn1,ctabtn2,codeblock,codeColor
})=>{
    return(
        <div className={`flex ${position} justify-between gap-10`}>
            {/* Section 1 */}
            <div className='w-[50%] flex flex-col gap-8'>
                {heading}
                <div className='text-richblack-300 font-bold'>
                    {subheading}
                </div>

                <div className='flex gap-7 mt-7'>
                        <CTAButton active={ctabtn1.active} linkto={ctabtn1.linkto}>
                            <div className='flex gap-2 items-center'>
                                {ctabtn1.btnText}
                                <FaArrowRight/>
                            </div>
                        </CTAButton>
                        <CTAButton active={ctabtn2.active} linkto={ctabtn2.linkto}>
                                {ctabtn2.btnText}
                        </CTAButton>
                </div>

            </div>

            {/* Section 2 */}
            <div className='flex w-[50%] h-fit text-[15px] py-4 lg:w-[500px] bg-[rgba(255,255,255,.1)]'>
                {/* HW - Bg gradient */}
                <div className='text-center flex flex-col w-[10%] text-richblue-400 font-inter font-bold'>
                    <p>1</p>
                    <p>2</p>
                    <p>3</p>
                    <p>4</p>
                    <p>5</p>
                    <p>6</p>
                    <p>7</p>
                    <p>8</p>
                    <p>9</p>
                    <p>10</p>
                    <p>11</p>
                </div>
                <div className={`w-[90%] flex flex-col gap-2 font-bold font-mono ${codeColor} pr-2`}>
                        <TypeAnimation
                        sequence={[codeblock,2000,""]}
                        repeat={Infinity}
                        style={
                            {
                                whiteSpace: "pre-line",
                                display:"block"
                            }
                        }
                        omitDeletionAnimation={true}
                        />
                </div>
            </div>

        </div>
    )
}

export default CodeBlocks;
